import { useState } from "react";
import { DefaultHeader } from "../Header";
import { NewValue } from "../NewValueForm";
import { ValueList } from "../ValueList";

export function StartedHome() {
  const [values, setValue] = useState([
    {
      title: "Roupas",
      type: "Saída",
      amount: "R$ 14,000",
    },
    {
      title: "Comida",
      type: "Saída",
      amount: "R$ 100",
    },
    {
      title: "Pagamento",
      type: "Entrada",
      amount: "R$ 25,000",
    },
    {
      title: "Rolês",
      type: "Saída",
      amount: "R$ 4,000",
    },
  ]);

  function addNewValue(newValue) {
    setValue([...values, newValue]);
  }

  function removeValue(valueName) {
    const newValues = values.filter((value) => value.title !== valueName);
    console.log(newValues);
  }

  removeValue();
  return (
    <>
      <DefaultHeader />
      <div className="flex">
        <NewValue addNewValue={addNewValue} />
        <ValueList valueList={values} />
      </div>
    </>
  );
}
