import { useState } from "react";
import "./index.css";

export function NewValue({ setValue }) {
  const [isName, setName] = useState("");
  const [isAmount, setAmount] = useState("");
  const [isType, setType] = useState("Entrada");

  function createNewValue(event) {
    event.preventDefault();

    const value = {
      isName,
      isAmount,
      isType,
    };
    setValue((oldValues) => {
      return [...(oldValues + value)];
    });
  }

  return (
    <form className="new-value-container" onSubmit={createNewValue}>
      <div className="separator">
        <p className="input_name">Descrição</p>
        <input
          type="text"
          className="valueDescritpion"
          value={isName}
          onChange={(event) => setName(event.target.value)}
        />
        <label htmlFor="ValueDescription" className="value_description_label">
          Ex: Compra de roupas
        </label>
      </div>
      <div className="buttons-container">
        <div className="separator">
          <label htmlFor="">Valor</label>
          <input
            type="number"
            name=""
            value={isAmount}
            onChange={(event) => setAmount(event.target.value)}
          />
        </div>
        <div className="separator">
          <label htmlFor="">Tipo de valor</label>
          <select
            name=""
            id=""
            onChange={(event) => setType(event.target.value)}>
            <option value="Entrada">Entrada</option>
            <option value="Saída">Saída</option>
          </select>
        </div>
        <button className="pink_button" type="submit">
          Inserir valor
        </button>
      </div>
    </form>
  );
}
