import { useState } from "react";
import "./index.css";

export function NewValue() {
  const [isName, setName] = useState("a");
  const [isAmount, setAmount] = useState("a");
  const [isType, setType] = useState("a");
  return (
    <form className="new-value-container">
      <p className="input_name">Descrição</p>
      <input
        type="text"
        className="valueDescritpion"
        value={isName}
        onChange={setName}
      />
      <label htmlFor="ValueDescription" className="value_description_label">
        Ex: Compra de roupas
      </label>
      <div className="buttons-container">
        <div className="separator">
          <label htmlFor="">Valor</label>
          <input type="number" name="" value={isAmount} onChange={setAmount} />
        </div>
        <div className="separator">
          <label htmlFor="">Tipo de valor</label>
          <select name="" id="" onChange={setType}>
            <option value={isType}>Entrada</option>
            <option value={isType}>Saída</option>
          </select>
        </div>
      </div>
    </form>
  );
}
